<?php 	include_once "koneksi.php";	// perhitungan haversine formula pada sintak SQL	$query = mysqli_query($con, "SELECT id_b, nama_b, lat_b, lng_b FROM bensineceran ORDER BY nama_b ASC");	$json = '{"bensineceran": [';		// create looping dech array in fetch	while ($row = mysqli_fetch_array($query)){	// quotation marks (") are not allowed by the json string, we will replace it with the` character	// strip_tag serves to remove html tags on strings		$char ='"';		$json .= 		'{			"id_b":"'.str_replace($char,'`',strip_tags($row['id_b'])).'", 			"nama_b":"'.str_replace($char,'`',strip_tags($row['nama_b'])).'",			"lat_b":"'.str_replace($char,'`',strip_tags($row['lat_b'])).'",			"lng_b":"'.str_replace($char,'`',strip_tags($row['lng_b'])).'"		},';	}	// omitted commas at the end of the array	$json = substr($json,0,strlen($json)-1);	$json .= ']}';	// print json	echo $json;		mysqli_close($con);	?>